        // Other methods

        /// <summary>Writes a sequence of bits to the stream, and returns this sequence backed by the stream's buffer.
        /// </summary>
        /// <param name="bitSize">The minimum number of bits in the sequence.</param>
        /// <returns>The bit sequence, with all bits set. The actual size of the sequence is a multiple of 8.
        /// </returns>
        public BitSequence WriteBitSequence(int bitSize)
        {
            Debug.Assert(bitSize > 0);
            int size = (bitSize >> 3) + ((bitSize & 0x07) != 0 ? 1 : 0);

            Expand(size);

            int remaining = _currentSegment.Count - _tail.Offset;
            if (size <= remaining)
            {
                // Expand above ensures _tail.Offset is not _currentSegment.Count.
                Span<byte> span = _currentSegment.AsSpan(_tail.Offset, size);
                span.Fill(255);
                _tail.Offset += size;
                Size += size;
                return new BitSequence(span);
            }
            else
            {
                Span<byte> firstSpan = _currentSegment.AsSpan(_tail.Offset);
                firstSpan.Fill(255);
                _currentSegment = _segmentList[++_tail.Segment];
                _tail.Offset = size - remaining;
                Size += size;
                Span<byte> secondSpan = _currentSegment.AsSpan(0, _tail.Offset);
                secondSpan.Fill(255);
                return new BitSequence(firstSpan, secondSpan);
            }
        }

        /// <summary>Computes the minimum number of bytes needed to write a variable-length size with the 2.0 encoding.
        /// </summary>
        /// <remarks>The parameter is a long and not a varulong because sizes and size-like values are usually passed
        /// around as signed integers, even though sizes cannot be negative and are encoded like varulong values.
        /// </remarks>
        internal static int GetSizeLength20(long size)
        {
            Debug.Assert(size >= 0);
            return 1 << GetVarULongEncodedSizeExponent((ulong)size);
        }

        // Constructor for protocol frame header and other non-encapsulated data.
        internal OutputStream(Encoding encoding, IList<ArraySegment<byte>> data, Position startAt = default)
        {
            _mainEncoding = encoding;
            Encoding = encoding;
            Encoding.CheckSupported();
            _format = default; // not used
            _segmentList = data;
            if (_segmentList.Count == 0)
            {
                _currentSegment = ArraySegment<byte>.Empty;
                _capacity = 0;
                Size = 0;
                _tail = default;
            }
            else
            {
                _tail = startAt;
                _currentSegment = _segmentList[_tail.Segment];
                Size = Distance(default);
                _capacity = 0;
                foreach (ArraySegment<byte> segment in _segmentList)
                {
                    _capacity += segment.Count;
                }
            }
        }

        // Constructor that starts an encapsulation.
        internal OutputStream(
            Encoding encoding,
            IList<ArraySegment<byte>> data,
            Position startAt,
            Encoding payloadEncoding,
            FormatType format)
            : this(encoding, data, startAt)
        {
            _format = format;
            _startPos = _tail;
            WriteEncapsulationHeader(payloadEncoding); // with placeholder for size
            if (payloadEncoding == Encoding.V20)
            {
                WriteByte(0); // Placeholder for the compression status
            }
            Encoding = payloadEncoding;
        }

        /// <summary>Computes the amount of data written from the start position to the current position and writes that
        /// size at the start position (as a fixed-length size). The size does not include its own encoded length.
        /// </summary>
        /// <param name="start">The start position.</param>
        /// <param name="sizeLength">The number of bytes used to marshal the size 1, 2 or 4.</param>
        internal void EndFixedLengthSize(Position start, int sizeLength = DefaultSizeLength)
        {
            Debug.Assert(start.Offset >= 0);
            if (OldEncoding)
            {
                RewriteFixedLengthSize11(Distance(start) - 4, start);
            }
            else
            {
                RewriteFixedLengthSize20(Distance(start) - sizeLength, start, sizeLength);
            }
        }

        /// <summary>Completes the current encapsulation (if any) and finishes off the underlying buffer. You should not
        /// write additional data to this output stream or its underlying buffer after calling Finish, however rewriting
        /// previous data (with for example <see cref="EndFixedLengthSize"/>) is fine.</summary>
        internal void Finish()
        {
            if (_startPos is Position startPos)
            {
                Encoding = _mainEncoding;
                int sizeLength = OldEncoding ? 4 : DefaultSizeLength;
                RewriteEncapsulationSize(Distance(startPos) - sizeLength, startPos);
            }

            Debug.Assert(_segmentList.Count - 1 == _tail.Segment);
            _segmentList[^1] = _segmentList[^1].Slice(0, _tail.Offset);
        }

        /// <summary>Writes a size on a fixed number of bytes at the given position of the stream.</summary>
        /// <param name="size">The size to write.</param>
        /// <param name="pos">The position to write to.</param>
        /// <param name="sizeLength">The number of bytes used to encode the size. Can be 1, 2 or 4.</param>
        internal void RewriteFixedLengthSize20(int size, Position pos, int sizeLength = DefaultSizeLength)
        {
            Debug.Assert(pos.Segment < _segmentList.Count);
            Debug.Assert(sizeLength == 1 || sizeLength == 2 || sizeLength == 4);

            Span<byte> data = stackalloc byte[sizeLength];
            data.WriteFixedLengthSize20(size);
            RewriteByteSpan(data, pos);
        }

        /// <summary>Returns the current position and writes placeholder for a fixed-length size value. The
        /// position must be used to rewrite the size later.</summary>
        /// <param name="sizeLength">The number of bytes reserved to write the fixed-length size.</param>
        /// <returns>The position before writing the size.</returns>
        internal Position StartFixedLengthSize(int sizeLength = DefaultSizeLength)
        {
            Position pos = _tail;
            WriteByteSpan(stackalloc byte[OldEncoding ? 4 : sizeLength]); // placeholder for future size
            return pos;
        }

        /// <summary>Writes a span of bytes. The stream capacity is expanded if required, the size and tail position are
        /// increased according to the span length.</summary>
        /// <param name="span">The data to write as a span of bytes.</param>
        internal void WriteByteSpan(ReadOnlySpan<byte> span)
        {
            int length = span.Length;
            Expand(length);
            Size += length;
            int offset = _tail.Offset;
            int remaining = _currentSegment.Count - offset;
            Debug.Assert(remaining > 0); // guaranteed by Expand
            int sz = Math.Min(length, remaining);
            if (length > remaining)
            {
                span.Slice(0, remaining).CopyTo(_currentSegment.AsSpan(offset, sz));
            }
            else
            {
                span.CopyTo(_currentSegment.AsSpan(offset, length));
            }
            _tail.Offset += sz;
            length -= sz;

            if (length > 0)
            {
                _currentSegment = _segmentList[++_tail.Segment];
                if (remaining == 0)
                {
                    span.CopyTo(_currentSegment.AsSpan(0, length));
                }
                else
                {
                    span.Slice(remaining, length).CopyTo(_currentSegment.AsSpan(0, length));
                }
                _tail.Offset = length;
            }
        }

        /// <summary>Writes an empty encapsulation.</summary>
        internal Position WriteEmptyEncapsulation(Encoding encoding)
        {
            encoding.CheckSupported();
            WriteEncapsulationHeader(size: encoding == Encoding.V20 ? 3 : 2, encoding);
            if (encoding == Encoding.V20)
            {
                WriteByte(0); // The compression status, 0 = not-compressed
            }
            return _tail;
        }

        internal void WriteEndpoint11(Endpoint endpoint)
        {
            Debug.Assert(OldEncoding);

            this.Write(endpoint.Transport);

            Position startPos = _tail;
            int sizeLength = 4;

            if (endpoint is OpaqueEndpoint opaqueEndpoint)
            {
                WriteEncapsulationHeader(opaqueEndpoint.ValueEncoding, sizeLength); // with placeholder for size
                WriteByteSpan(opaqueEndpoint.Value.Span); // WriteByteSpan is not encoding-sensitive
            }
            else
            {
                WriteEncapsulationHeader(Encoding, sizeLength); // with placeholder for size
                if (endpoint.Protocol == Protocol.Ice1)
                {
                    endpoint.WriteOptions11(this);
                }
                else
                {
                    WriteString(endpoint.Data.Host);
                    WriteUShort(endpoint.Data.Port);
                    WriteSequence(endpoint.Data.Options, IceWriterFromString);
                }
            }
            RewriteEncapsulationSize(Distance(startPos) - sizeLength, startPos, sizeLength);
        }

        internal void WriteBinaryContextEntry(int key, ReadOnlySpan<byte> value)
        {
            WriteVarInt(key);
            WriteSize(value.Length);
            WriteByteSpan(value);
        }

        internal void WriteBinaryContextEntry<T>(int key, T value, OutputStreamWriter<T> writer)
        {
            WriteVarInt(key);
            Position pos = StartFixedLengthSize(2); // 2-bytes size place holder
            writer(this, value);
            EndFixedLengthSize(pos, 2);
        }

        private static int Distance(IList<ArraySegment<byte>> data, Position start, Position end)
        {
            // If both the start and end position are in the same array segment just
            // compute the offsets distance.
            if (start.Segment == end.Segment)
            {
                return end.Offset - start.Offset;
            }

            // If start and end position are in different segments we need to accumulate the
            // size from start offset to the end of the start segment, the size of the intermediary
            // segments, and the current offset into the last segment.
            ArraySegment<byte> segment = data[start.Segment];
            int size = segment.Count - start.Offset;
            for (int i = start.Segment + 1; i < end.Segment; ++i)
            {
                checked
                {
                    size += data[i].Count;
                }
            }
            checked
            {
                return size + end.Offset;
            }
        }

        /// <summary>Gets the minimum number of bytes needed to encode a long value with the varlong encoding as an
        /// exponent of 2.</summary>
        /// <param name="value">The value to encode.</param>
        /// <returns>N where 2^N is the number of bytes needed to encode value with Ice's varlong encoding.</returns>
        private static int GetVarLongEncodedSizeExponent(long value)
        {
            if (value < EncodingDefinitions.VarLongMinValue || value > EncodingDefinitions.VarLongMaxValue)
            {
                throw new ArgumentOutOfRangeException($"varlong value '{value}' is out of range", nameof(value));
            }

            return (value << 2) switch
            {
                long b when b >= sbyte.MinValue && b <= sbyte.MaxValue => 0,
                long s when s >= short.MinValue && s <= short.MaxValue => 1,
                long i when i >= int.MinValue && i <= int.MaxValue => 2,
                _ => 3
            };
        }

        /// <summary>Gets the mimimum number of bytes needed to encode a long value with the varulong encoding as an
        /// exponent of 2.</summary>
        /// <param name="value">The value to encode.</param>
        /// <returns>N where 2^N is the number of bytes needed to encode value with varulong encoding.</returns>
        private static int GetVarULongEncodedSizeExponent(ulong value)
        {
            if (value > EncodingDefinitions.VarULongMaxValue)
            {
                throw new ArgumentOutOfRangeException($"varulong value '{value}' is out of range", nameof(value));
            }

            return (value << 2) switch
            {
                ulong b when b <= byte.MaxValue => 0,
                ulong s when s <= ushort.MaxValue => 1,
                ulong i when i <= uint.MaxValue => 2,
                _ => 3
            };
        }

        /// <summary>Returns the distance in bytes from start position to the current position.</summary>
        /// <param name="start">The start position from where to calculate distance to current position.</param>
        /// <returns>The distance in bytes from the current position to the start position.</returns>
        private int Distance(Position start)
        {
            Debug.Assert(_tail.Segment > start.Segment ||
                        (_tail.Segment == start.Segment && _tail.Offset >= start.Offset));

            return Distance(_segmentList, start, _tail);
        }

        /// <summary>Expands the stream to make room for more data. If the bytes remaining in the stream are not enough
        /// to hold the given number of bytes, allocates a new byte array. The caller should then consume the new bytes
        /// immediately; calling Expand repeatedly is not supported.</summary>
        /// <param name="n">The number of bytes to accommodate in the stream.</param>
        private void Expand(int n)
        {
            Debug.Assert(n > 0);
            int remaining = _capacity - Size;
            if (n > remaining)
            {
                int size = Math.Max(DefaultSegmentSize, _currentSegment.Count * 2);
                size = Math.Max(n - remaining, size);
                byte[] buffer = new byte[size];
                _segmentList.Add(buffer);
                if (_segmentList.Count == 1) // First Expand for a new OutputStream constructed with no buffer.
                {
                    Debug.Assert(_currentSegment.Count == 0);
                    _currentSegment = buffer;
                }
                else if (remaining == 0)
                {
                    // Patch _tail to point to the first byte in the new buffer.
                    Debug.Assert(_tail.Offset == _currentSegment.Count);
                    _currentSegment = buffer;
                    _tail.Segment++;
                    _tail.Offset = 0;
                }
                _capacity += buffer.Length;
            }

            // Once Expand returns, _tail points to a writeable byte.
            Debug.Assert(_tail.Offset < _currentSegment.Count);
        }

        /// <summary>Computes the minimum number of bytes needed to write a variable-length size with the current
        /// encoding.</summary>
        /// <param name="size">The size.</param>
        /// <returns>The minimum number of bytes.</returns>
        private int GetSizeLength(int size) => OldEncoding ? (size < 255 ? 1 : 5) : GetSizeLength20(size);

        /// <summary>Writes a byte at a given position of the stream.</summary>
        /// <param name="v">The byte value to write.</param>
        /// <param name="pos">The position to write to.</param>
        private void RewriteByte(byte v, Position pos)
        {
            ArraySegment<byte> segment = _segmentList[pos.Segment];
            if (pos.Offset < segment.Count)
            {
                segment[pos.Offset] = v;
            }
            else
            {
                // (segN, segN.Count) points to the same byte as (segN + 1, 0)
                Debug.Assert(pos.Offset == segment.Count);
                segment = _segmentList[pos.Segment + 1];
                segment[0] = v;
            }
        }

        /// <summary>Rewrites an encapsulation size on a fixed number of bytes at the given position of the stream.
        /// </summary>
        /// <param name="size">The number of bytes in the encapsulation, without taking into account the bytes for the
        /// size itself.</param>
        /// <param name="pos">The position to write to.</param>
        /// <param name="sizeLength">The number of bytes used to encode the size with the 2.0 encoding. Can be 1, 2 or
        /// 4.</param>
        private void RewriteEncapsulationSize(int size, Position pos, int sizeLength = DefaultSizeLength)
        {
            if (OldEncoding)
            {
                // With the 1.1 encoding, sizeLength is always 4 bytes and the encoded size includes this sizeLength.
                RewriteFixedLengthSize11(size + 4, pos);
            }
            else
            {
                RewriteFixedLengthSize20(size, pos, sizeLength);
            }
        }

        /// <summary>Writes a size on 4 bytes at the given position of the stream.</summary>
        /// <param name="size">The size to write.</param>
        /// <param name="pos">The position to write to.</param>
        internal void RewriteFixedLengthSize11(int size, Position pos)
        {
            Debug.Assert(pos.Segment < _segmentList.Count);

            Span<byte> data = stackalloc byte[4];
            MemoryMarshal.Write(data, ref size);
            RewriteByteSpan(data, pos);
        }

        private void RewriteByteSpan(Span<byte> data, Position pos)
        {
            ArraySegment<byte> segment = _segmentList[pos.Segment];
            int remaining = Math.Min(data.Length, segment.Count - pos.Offset);
            if (remaining > 0)
            {
                data.Slice(0, remaining).CopyTo(segment.AsSpan(pos.Offset, remaining));
            }

            if (remaining < data.Length)
            {
                segment = _segmentList[pos.Segment + 1];
                data[remaining..].CopyTo(segment.AsSpan(0, data.Length - remaining));
            }
        }

        /// <summary>Writes an encapsulation header.</summary>
        /// <param name="size">The size of the encapsulation, in bytes. This size does not include the length of the
        /// encoded size itself.</param>
        /// <param name="encoding">The encoding of the new encapsulation.</param>
        internal void WriteEncapsulationHeader(int size, Encoding encoding)
        {
            if (OldEncoding)
            {
                WriteInt(size + 4); // the size length is included in the encoded size with the 1.1 encoding.
            }
            else
            {
                WriteSize(size);
            }

            WriteByte(encoding.Major);
            WriteByte(encoding.Minor);
        }

        /// <summary>Writes an encapsulation header with a placeholder size.</summary>
        /// <param name="encoding">The encoding of the new encapsulation.</param>
        /// <param name="sizeLength">The number of bytes used to encode the size, used only with the 2.0 encoding. Can
        /// be 1, 2 or 4.</param>
        private void WriteEncapsulationHeader(Encoding encoding, int sizeLength = DefaultSizeLength)
        {
            Debug.Assert(sizeLength == 1 || sizeLength == 2 || sizeLength == 4);
            WriteByteSpan(stackalloc byte[OldEncoding ? 4 : sizeLength]); // placeholder for future size
            WriteByte(encoding.Major);
            WriteByte(encoding.Minor);
        }

        /// <summary>Writes a fixed-size numeric value to the stream.</summary>
        /// <param name="v">The numeric value to write to the stream.</param>
        private void WriteFixedSizeNumeric<T>(T v) where T : struct
        {
            int elementSize = Unsafe.SizeOf<T>();
            Span<byte> data = stackalloc byte[elementSize];
            MemoryMarshal.Write(data, ref v);
            WriteByteSpan(data);
        }

        /// <summary>Writes the header for a tagged parameter or data member.</summary>
        /// <param name="tag">The numeric tag associated with the parameter or data member.</param>
        /// <param name="format">The tag format.</param>
        private void WriteTaggedParamHeader(int tag, EncodingDefinitions.TagFormat format)
        {
            Debug.Assert(InEncapsulation);
            Debug.Assert(format != EncodingDefinitions.TagFormat.VInt); // VInt cannot be marshaled

            int v = (int)format;
            if (tag < 30)
            {
                v |= tag << 3;
                WriteByte((byte)v);
            }
            else
            {
                v |= 0x0F0; // tag = 30
                WriteByte((byte)v);
                WriteSize(tag);
            }
            if (_current.InstanceType != InstanceType.None)
            {
                _current.SliceFlags |= EncodingDefinitions.SliceFlags.HasTaggedMembers;
            }
        }
    }
}
